// https://tympanus.net/codrops/2019/10/23/making-gooey-image-hover-effects-with-three-js/
import * as THREE from "three";
import { TweenMax } from "gsap";
import vertexShader from "./vertexShader.vs.glsl";
import fragmentShader from "./fragmentShader.fs.glsl";

export default class Figure {
  constructor(scene, cb) {
    this.$image = document.querySelector(".tile__image");
    this.scene = scene;
    this.callback = cb;

    this.loader = new THREE.TextureLoader();

    this.image = this.loader.load(this.$image.src, () => {
      this.start();
    });
    this.hover = this.loader.load(this.$image.dataset.hover);
    this.$image.style.opacity = 0;
    this.sizes = new THREE.Vector2(0, 0);
    this.offset = new THREE.Vector2(0, 0);

    this.mouse = new THREE.Vector2(0, 0);
    window.addEventListener("mousemove", e => {
      this.onMouseMove(e);
    });
  }

  start() {
    this.getSizes();
    this.createMesh();
    this.callback();
  }

  getSizes() {
    const { width, height, top, left } = this.$image.getBoundingClientRect();

    this.sizes.set(width, height);
    // calculate the distance between cente rofthe screne and obj on the page
    this.offset.set(
      left - window.innerWidth / 2 + width / 2,
      -top + window.innerHeight / 2 - height / 2
    );
  }

  createMesh() {
    this.uniforms = {
      u_image: { type: "t", value: this.image },
      u_imagehover: { type: "t", value: this.hover },
      u_mouse: { value: this.mouse },
      u_time: { value: 0 },
      u_res: {
        value: new THREE.Vector2(window.innerWidth, window.innerHeight)
      },
      u_horizontal: { value: this.mouse.x }
    };

    this.geometry = new THREE.PlaneBufferGeometry(1, 1, 1, 1);
    this.material = new THREE.ShaderMaterial({
      uniforms: this.uniforms,
      vertexShader: vertexShader,
      fragmentShader: fragmentShader,
      defines: {
        PR: window.devicePixelRatio.toFixed(1)
      }
    });
    this.mesh = new THREE.Mesh(this.geometry, this.material);

    this.mesh.position.set(this.offset.x, this.offset.y, 0);
    this.mesh.scale.set(this.sizes.x, this.sizes.y, 1);

    this.scene.add(this.mesh);
  }

  onMouseMove(event) {
    TweenMax.to(this.mouse, 0.5, {
      x: (event.clientX / window.innerWidth) * 2 - 1,
      y: -(event.clientY / window.innerHeight) * 2 + 1
    });

    TweenMax.to(this.mesh.rotation, 0.5, {
      x: -this.mouse.y * 0.3,
      y: this.mouse.x * (Math.PI / 6)
    });
  }

  update() {
    this.uniforms.u_time.value += 0.01;
  }
}
